insert into products (id, name, description, price, prescription, precaution, side_effect, presentation, stock, unit_measure, subcategory_id, state, updated_at) values (42, 'Systane Balance Emulsión Oftálmica - Frasco 10 ML','',123.7,'VENTA LIBRE','Advertencias: Para uso externo. Cuando se usa este producto, se deberán tener en cuenta las siguientes precauciones: no tocar ninguna superficie con la punta del envase para evitar contaminación. Volver a tapar el envase después de cada uso. Discontinuar el uso del producto y consultar de inmediato con un médico, si: Se experimenta dolor ocular. Se experimentan cambios en la visión. El cuadro de enrojecimiento o irritación ocular empeora, persiste o dura más de 72 horas. Mantener fuera del alcance de los niños.','No usar: si este producto cambia de color. Si se es sensible a cualquiera de los componentes de este producto.','FRASCO',1,'ML',17455,'A',CURRENT_TIMESTAMP);
insert into products (id, name, description, price, prescription, precaution, side_effect, presentation, stock, unit_measure, subcategory_id, state, updated_at) values (43, 'Optive Fusión Solución Oftálmica','',95,'VENTA LIBRE','ADVERTENCIAS Y PRECAUCIONES: Suspender su uso y consulte a un médico si se experimenta dolor en los ojos, cambios en la visión, enrojecimiento o irritación ocular continua, o si la condición empeora o persiste por más de 72 horas. Manténganse fuera del alcance de los niños. En caso sea ingerido, obtenga ayuda médica inmediata o comuníquese con el centro de salud más cercano. Usar sólo si los sellos se encuentran intactos. Usar antes de la fecha de vencimiento indicada en el envase y caja del frasco.','Hipersensibilidad a cualquiera de los ingredientes de la formulación de Optive Fusion®. Sólo para uso externo. Para evitar contaminación o posible lesión ocular, no tocar la punta del frasco con ninguna superficie, ni con el ojo. Vuelva a colocar la tapa después de usar. No usar si la solución cambia de color o llega a estar turbia.','FRASCO',1,'UN',17455,'A',CURRENT_TIMESTAMP);
insert into products (id, name, description, price, prescription, precaution, side_effect, presentation, stock, unit_measure, subcategory_id, state, updated_at) values (44, 'Colirio Eyemo 0.05% Solucion oftálmica','',11.2,'VENTA LIBRE','ADVERTENCIAS Y PRECAUCIONES: Se debe evaluar la relación beneficio riesgo en: Pacientes con enfermedades cardiovasculares graves (ejemplo: enfermedades coronarias). Pacientes con feocromocitoma. Pacientes con alteraciones metabólicas (ejemplo: hipertiroidismo, diabetes mellitus). Pacientes bajo tratamiento con inhibidores de la MAO u otros elevadores potenciales de la presión arterial. Pacientes con rinitis seca y o queratoconjuntivitis. Hipertensión arterial. En caso de que el paciente utilice lentes de contacto, antes de la administración del medicamento se debe retirar. En algunas ocasiones, la irritación o el enrojecimiento es debido a infecciones oculares serias como infección, cuerpo extraño o traumatismo químico corneal, requiriendo la evaluación clínica. Se recomienda evaluar la situación clínica en mayores de 65 años. Como en el caso de otros productos oftálmicos existe la posibilidad de absorción sistémica. Para mayor información. Leer Inserto.','No administrar este medicamento en los siguientes casos: Hipersensibilidad al principio activo o a alguno de los excipientes. Pacientes con glaucoma de ángulo estrecho. Niños menores de 2 años.','FRASCO',1,'ML',17455,'A',CURRENT_TIMESTAMP);
insert into products (id, name, description, price, prescription, precaution, side_effect, presentation, stock, unit_measure, subcategory_id, state, updated_at) values (45, 'Optive Solución Oftálmica - Frasco 15 ML','',70.6,'VENTA LIBRE','ADVERTENCIAS Y PRECAUCIONES: Para no contaminar el envase no permita que la punta toque alguna superficie. En caso de sentir dolor, algún cambio en su visión, enrojecimiento, irritación ó si su condición empeora ó los síntomas persisten durante más de 72 horas, deje de usar el producto y consulte a su oftalmólogo. Si la solución cambia de color o se enturbia no la use.','OPTIVE® (glicerina y carboximetilcelulosa) solución oftálmica está contraindicada en pacientes con hipersensibilidad conocida a los componentes de la formula del producto.','FRASCO',1,'ML',17455,'A',CURRENT_TIMESTAMP);
