insert into products (id, name, description, price, prescription, precaution, side_effect, presentation, stock, unit_measure, subcategory_id, state, updated_at) values (47, 'Biotears 0.3% - 0.1% Solución Oftálmica','',44.7,'VENTA LIBRE','ADVERTENCIAS Y PRECAUCIONES: Si presenta dolor ocular, visión borrosa, enrojecimiento o irritación persistente, o si los síntomas no mejoran dentro de los tres días, el paciente deberá suspender el uso de las gotas para los ojos y ponerse en contacto con su médico. Si la solución cambia de color o se vuelve turbia, no se debe utilizar por más tiempo.','Hipersensibilidad a los principios activos o a alguno de los excipientes.','FRASCO',1,'ML',17455,'A',CURRENT_TIMESTAMP);
